mapper.select:
select * from generator_rule t
where 1=1

       t.tenant_id = #{p.tenantId}


       t.creation_time = #{p.creationTime}


       t.creation_user_id = #{p.creationUserId}


       t.deletion_time = #{p.deletionTime}


       t.deletion_user_id = #{p.deletionUserId}


       t.last_modify_time = #{p.lastModifyTime}


       t.last_modify_user_id = #{p.lastModifyUserId}


       t.author_name = #{p.authorName}


       t.email = #{p.email}


       t.module_name = #{p.moduleName}


       t.package_name = #{p.packageName}


       t.ui_template = #{p.uiTemplate}
